mapper.update:
update account_card_info aci
   set aci.card_name = #{cardName}
      ,aci.bankcd = #{bankCd}
      ,aci.expired_ym = #{expiredYm}
      ,aci.use_yn = #{useYn}
      ,aci.credit_yn = #{creditYn}
      ,aci.selected_main = #{selectedMain}
      ,aci.limit_amt = #{limitAmt}
      ,aci.company = #{company}
 where aci.card_no = #{cardNo}
