mapper.select:
select concat(substr(acs.ymd, 1, 4), '-', substr(acs.ymd, 5, 2),'-', substr(acs.ymd, 7, 2)) as ymd ,concat(substr(acs.times, 1, 2), ':', substr(acs.times, 3, 2),':', substr(acs.times, 5, 2)) as times ,acs.approv_num as approv_num ,acs.card_no as card_no ,aci.card_name as card_nm ,acs.usage as usage_cd ,coalesce((select asd.attribute03 from account_setting_detail asd where asd.header_id = 4 and asd.code = acs.usage), 'EEEEEE') as usage_color ,(select x.name from account_setting_detail x where x.header_id = 4 and x.code = acs.usage) as usage_nm ,acs.amount as amount ,acs.remark as remark from account_card_statement acs left join account_card_info aci on acs.card_no = aci.card_no where 1=1
and acs.ymd between #{ymdFrom} and #{ymdTo}
and acs.card_no = #{cardNo}
and acs.remark like concat('%', #{remark}, '%')
and acs.usage like concat('%', #{usage}, '%')
and acs.amount >= #{amountFrom}
and acs.amount <= #{amountTo}

mapper.delete:
delete from account_import_file
 where file_id = #{fileId}
   and file_type = #{fileType}

mapper.insert:
insert account_card_statement_exceltmp
values (
 #{fileHash}
,#{ymd}
,#{approvNum}
,(select aci.card_no
    from account_card_info aci
   where aci.card_no like concat(#{cardNo}, '%')
 )
,(select aci.card_name
    from account_card_info aci
   where aci.card_no like concat(#{cardNo}, '%')
 )
,#{usageCd}
,(select asd.name
    from account_setting_detail asd
   where asd.header_id
     and asd.code = #{usageCd})
,#{amount}
,#{remark}
,#{times}
)

insert account_card_statement (
 `ymd`
,`approv_num`
,`card_no`
,`usage`
,`amount`
,`remark`
,`times`
)
values (
 replace(#{ymd}, '.', '')
,#{approvNum}
,#{cardNo}
,#{usageCd}
,#{amount}
,#{remark}
,replace(#{times}, ':', '')
)
on duplicate key
update `usage` = #{usageCd}
      , remark = #{remark}

mapper.select:
select sum(a.amount)
  from (select acs.amount
          from account_card_statement acs
         where acs.card_no = #{cardNo}
           and substr(acs.ymd, 1, 6) = #{ym}
        union all
        select acss.amount
          from account_card_statement_sms acss
         where acss.card_no = #{cardNo}
           and substr(acss.ymd, 1, 6) = #{ym}
       ) a
